        if (featuresCount > 0) {
          const confirmed = confirm(`This category has ${featuresCount} features. Delete anyway?`);
          if (!confirmed) return;
        }

        const index = this.categories.findIndex((c) => c.id === id);
        this.categories.splice(index, 1);
