import { defineStore } from 'pinia';
import type { Category, Sprint, Feature, Checkpoint, Toast } from '../types';

interface RoadmapState {
  categories: Category[];
  sprints: Sprint[];
  features: Feature[];
  checkpoints: Checkpoint[];
  toasts: Toast[];
  loading: boolean;
  error: string | null;
  lastAction: {
    type: 'move' | 'create' | 'update' | 'delete';
    data: any;
  } | null;
}

export const useRoadmapStore = defineStore('roadmap', {
  state: (): RoadmapState => ({
