@import "tailwindcss";

/* Custom theme configuration */
@theme {
  --color-order-light: #e0f2fe;
  --color-order: #0ea5e9;
  --color-order-dark: #0369a1;

  --color-validation-light: #f0fdf4;
  --color-validation: #10b981;
  --color-validation-dark: #047857;

  --color-inventory-light: #fef3c7;
  --color-inventory: #f59e0b;
  --color-inventory-dark: #d97706;

  --color-payment-light: #ede9fe;
  --color-payment: #8b5cf6;
  --color-payment-dark: #6d28d9;

  --color-orchestration-light: #fce7f3;
  --color-orchestration: #ec4899;
  --color-orchestration-dark: #be185d;

  --color-notification-light: #dbeafe;
  --color-notification: #3b82f6;
  --color-notification-dark: #1e40af;

  --color-ui-light: #fee2e2;
  --color-ui: #ef4444;
  --color-ui-dark: #b91c1c;

  --color-reporting-light: #e0e7ff;
  --color-reporting: #6366f1;
  --color-reporting-dark: #4338ca;

  --color-exceptional-light: #fef9c3;
  --color-exceptional: #eab308;
  --color-exceptional-dark: #a16207;
}

/* Custom base styles */
body {
  min-height: 100vh;
  background-color: var(--color-gray-50);
}

#app {
  min-height: 100vh;
}

