.cat-ui::before { background: var(--accent-teal); }
.cat-ui:hover { background: rgba(20, 184, 166, 0.2); border-color: var(--accent-teal); }

/* Tooltip */
.tooltip {
    position: fixed;
    padding: 0.75rem 1rem;
    background: var(--bg-card);
    border: 1px solid var(--border);
    border-radius: 8px;
    font-size: 0.8rem;
    color: var(--text-primary);
    box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.5);
    z-index: 1000;
    pointer-events: none;
    opacity: 0;
    transition: opacity 0.2s ease;
    max-width: 350px;
}

.tooltip.visible { opacity: 1; }

.tooltip-title {
    font-weight: 600;
    margin-bottom: 0.4rem;
    color: var(--accent-cyan);
}

.tooltip-module {
    font-size: 0.7rem;
    color: var(--text-muted);
    margin-bottom: 0.3rem;
}

.tooltip-detail {
    font-size: 0.7rem;
    color: var(--text-secondary);
    line-height: 1.4;
    border-top: 1px solid var(--border);
    padding-top: 0.4rem;
    margin-top: 0.3rem;
}

.tooltip-assignee {
    font-size: 0.65rem;
    color: var(--accent-purple);
    margin-top: 0.3rem;
}

.tooltip-hint {
    font-size: 0.6rem;
    color: var(--accent-green);
    margin-top: 0.4rem;
    padding-top: 0.3rem;
    border-top: 1px solid var(--border);
}

/* Toast notification */
.toast {
    position: fixed;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%) translateY(100px);
    padding: 0.75rem 1.5rem;
    background: var(--bg-card);
    border: 1px solid var(--accent-green);
    border-radius: 8px;
    font-size: 0.85rem;
    color: var(--accent-green);
    box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.5);
    z-index: 2000;
    opacity: 0;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.toast.visible {
    opacity: 1;
    transform: translateX(-50%) translateY(0);
}

/* Undo button */
.undo-container {
    position: fixed;
    bottom: 2rem;
    right: 2rem;
    z-index: 1500;
}

.undo-btn {
    display: none;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.25rem;
    background: var(--bg-card);
    border: 1px solid var(--accent-purple);
    border-radius: 8px;
    color: var(--accent-purple);
    font-family: 'DM Sans', sans-serif;
    font-size: 0.85rem;
    cursor: pointer;
    transition: all 0.3s ease;
}

.undo-btn.visible { display: flex; }

.undo-btn:hover {
    background: rgba(168, 85, 247, 0.2);
    transform: translateY(-2px);
}

/* Section dividers */
.section-divider {
    grid-column: 1 / -1;
    padding: 0.75rem 1rem;
    background: linear-gradient(90deg, rgba(168, 85, 247, 0.1) 0%, transparent 50%);
    border-bottom: 1px solid var(--accent-purple);
    font-family: 'Space Mono', monospace;
    font-size: 0.75rem;
    color: var(--accent-purple);
    text-transform: uppercase;
    letter-spacing: 0.1em;
}

/* Control Panel */
.control-panel {
    position: fixed;
    top: 1.5rem;
    right: 1.5rem;
    display: flex;
    gap: 0.75rem;
    z-index: 1500;
}

.control-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.75rem 1.25rem;
    background: var(--bg-card);
    border: 1px solid var(--border);
    border-radius: 8px;
    color: var(--text-primary);
