@import "tailwindcss";
@import url('https://fonts.googleapis.com/css2?family=DM+Sans:wght@400;500;700&family=Space+Mono:wght@400;700&display=swap');

/* CSS Custom Properties - Dark Theme */
:root {
  --bg-dark: #0a0a0f;
  --bg-card: #12121a;
  --bg-hover: #1a1a25;
  --accent-cyan: #00d4ff;
  --accent-purple: #a855f7;
  --accent-pink: #ec4899;
  --accent-orange: #f97316;
  --accent-green: #22c55e;
  --accent-yellow: #eab308;
  --accent-blue: #3b82f6;
  --accent-red: #ef4444;
  --accent-teal: #14b8a6;
  --text-primary: #f8fafc;
  --text-secondary: #94a3b8;
  --text-muted: #64748b;
  --border: #1e293b;
}

/* Custom theme configuration for Tailwind */
@theme {
  /* Category colors */
  --color-order-light: rgba(0, 212, 255, 0.1);
  --color-order: #00d4ff;
  --color-order-dark: #0891b2;
  --color-order-text: #7dd3fc;

  --color-validation-light: rgba(168, 85, 247, 0.1);
  --color-validation: #a855f7;
  --color-validation-dark: #7c3aed;
  --color-validation-text: #c4b5fd;

  --color-inventory-light: rgba(34, 197, 94, 0.1);
  --color-inventory: #22c55e;
  --color-inventory-dark: #16a34a;
  --color-inventory-text: #86efac;

  --color-payment-light: rgba(249, 115, 22, 0.1);
  --color-payment: #f97316;
  --color-payment-dark: #ea580c;
  --color-payment-text: #fdba74;

  --color-orchestration-light: rgba(236, 72, 153, 0.1);
  --color-orchestration: #ec4899;
  --color-orchestration-dark: #db2777;
  --color-orchestration-text: #f9a8d4;

  --color-notification-light: rgba(234, 179, 8, 0.1);
  --color-notification: #eab308;
  --color-notification-dark: #ca8a04;
  --color-notification-text: #fde047;

  --color-ui-light: rgba(20, 184, 166, 0.1);
  --color-ui: #14b8a6;
  --color-ui-dark: #0d9488;
  --color-ui-text: #5eead4;

  --color-reporting-light: rgba(59, 130, 246, 0.1);
  --color-reporting: #3b82f6;
  --color-reporting-dark: #2563eb;
  --color-reporting-text: #93c5fd;

  --color-exceptional-light: rgba(239, 68, 68, 0.1);
  --color-exceptional: #ef4444;
  --color-exceptional-dark: #dc2626;
  --color-exceptional-text: #fca5a5;
}

/* Reset and base styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html, body {
  min-height: 100vh;
}

body {
  font-family: 'DM Sans', sans-serif;
  background: var(--bg-dark);
  color: var(--text-primary);
  overflow-x: auto;
}

#app {
  min-height: 100vh;
  position: relative;
  z-index: 1;
}

/* Noise overlay effect */
.noise-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
  opacity: 0.03;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 400 400' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
  z-index: 1000;
}

/* Gradient background */
.gradient-bg {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background:
    radial-gradient(ellipse 80% 50% at 20% 40%, rgba(168, 85, 247, 0.15) 0%, transparent 50%),
    radial-gradient(ellipse 60% 40% at 80% 20%, rgba(0, 212, 255, 0.1) 0%, transparent 50%),
    radial-gradient(ellipse 50% 60% at 60% 80%, rgba(236, 72, 153, 0.08) 0%, transparent 50%);
  pointer-events: none;
  z-index: 0;
}

/* Monospace font for numbers */
.font-mono {
  font-family: 'Space Mono', monospace;
}

/* Glassmorphism card styles */
.glass-card {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 8px;
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
}

.glass-card:hover {
  border-color: rgba(0, 212, 255, 0.3);
}

/* Button styles */
.btn {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.25rem;
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 8px;
  color: var(--text-primary);
  font-family: 'DM Sans', sans-serif;
  font-size: 0.85rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
}

.btn:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 20px -8px rgba(0, 0, 0, 0.5);
}

.btn-cyan {
  border-color: var(--accent-cyan);
  color: var(--accent-cyan);
}

.btn-cyan:hover {
  background: rgba(0, 212, 255, 0.15);
}

.btn-green {
  border-color: var(--accent-green);
  color: var(--accent-green);
}

.btn-green:hover {
  background: rgba(34, 197, 94, 0.15);
}

.btn-purple {
  border-color: var(--accent-purple);
  color: var(--accent-purple);
}

.btn-purple:hover {
  background: rgba(168, 85, 247, 0.15);
}

.btn-orange {
  border-color: var(--accent-orange);
  color: var(--accent-orange);
}

.btn-orange:hover {
  background: rgba(249, 115, 22, 0.15);
}

.btn-red {
  border-color: var(--accent-red);
  color: var(--accent-red);
}

.btn-red:hover {
  background: rgba(239, 68, 68, 0.15);
}

.btn-primary {
  background: var(--accent-cyan);
  color: var(--bg-dark);
  border-color: var(--accent-cyan);
  font-weight: 600;
}

.btn-primary:hover {
  background: #00e5ff;
  box-shadow: 0 4px 12px rgba(0, 212, 255, 0.4);
}

/* Form input styles */
.form-input {
  width: 100%;
  padding: 0.75rem;
  background: var(--bg-dark);
  border: 1px solid var(--border);
  border-radius: 8px;
  color: var(--text-primary);
  font-family: inherit;
  font-size: 0.9rem;
  transition: border-color 0.2s ease;
}

.form-input:focus {
  outline: none;
  border-color: var(--accent-cyan);
  box-shadow: 0 0 0 3px rgba(0, 212, 255, 0.1);
}

.form-input::placeholder {
  color: var(--text-muted);
}

.form-label {
  display: block;
  margin-bottom: 0.5rem;
  color: var(--text-secondary);
  font-size: 0.85rem;
  font-weight: 500;
}

/* Modal backdrop */
.modal-backdrop {
  position: fixed;
  inset: 0;
  background: rgba(10, 10, 15, 0.9);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 50;
}

/* Modal content */
.modal-content {
  background: var(--bg-card);
  border: 1px solid var(--border);
  border-radius: 12px;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.5);
  max-width: 500px;
  width: 90%;
}

.modal-header {
  padding: 1.5rem;
  border-bottom: 1px solid var(--border);
}

.modal-title {
  color: var(--accent-cyan);
  font-family: 'Space Mono', monospace;
  font-size: 1.125rem;
  text-transform: uppercase;
  letter-spacing: 0.1em;
}

.modal-body {
  padding: 1.5rem;
}

.modal-footer {
  padding: 1rem 1.5rem;
  border-top: 1px solid var(--border);
  display: flex;
  justify-content: flex-end;
  gap: 0.75rem;
}

/* Sprint label styles */
.sprint-label {
  font-family: 'Space Mono', monospace;
  font-size: 0.8rem;
  font-weight: 700;
  color: var(--text-primary);
  padding: 0.4rem 0.8rem;
  background: linear-gradient(135deg, rgba(0, 212, 255, 0.2) 0%, rgba(168, 85, 247, 0.2) 100%);
  border-radius: 6px;
  display: inline-block;
}

.sprint-label.backlog {
  background: linear-gradient(135deg, rgba(100, 116, 139, 0.3) 0%, rgba(71, 85, 105, 0.3) 100%);
  color: var(--text-muted);
}

/* Category feature card colors */
.cat-order {
  background: rgba(0, 212, 255, 0.1);
  border: 1px solid rgba(0, 212, 255, 0.3);
  color: #7dd3fc;
}
.cat-order::before { background: var(--accent-cyan); }
.cat-order:hover { background: rgba(0, 212, 255, 0.2); border-color: var(--accent-cyan); }

.cat-validation {
  background: rgba(168, 85, 247, 0.1);
  border: 1px solid rgba(168, 85, 247, 0.3);
  color: #c4b5fd;
}
.cat-validation::before { background: var(--accent-purple); }
.cat-validation:hover { background: rgba(168, 85, 247, 0.2); border-color: var(--accent-purple); }

.cat-inventory {
  background: rgba(34, 197, 94, 0.1);
  border: 1px solid rgba(34, 197, 94, 0.3);
  color: #86efac;
}
.cat-inventory::before { background: var(--accent-green); }
.cat-inventory:hover { background: rgba(34, 197, 94, 0.2); border-color: var(--accent-green); }

.cat-payment {
  background: rgba(249, 115, 22, 0.1);
  border: 1px solid rgba(249, 115, 22, 0.3);
  color: #fdba74;
}
.cat-payment::before { background: var(--accent-orange); }
.cat-payment:hover { background: rgba(249, 115, 22, 0.2); border-color: var(--accent-orange); }

.cat-orchestration {
  background: rgba(236, 72, 153, 0.1);
  border: 1px solid rgba(236, 72, 153, 0.3);
  color: #f9a8d4;
}
.cat-orchestration::before { background: var(--accent-pink); }
.cat-orchestration:hover { background: rgba(236, 72, 153, 0.2); border-color: var(--accent-pink); }

.cat-notification {
  background: rgba(234, 179, 8, 0.1);
  border: 1px solid rgba(234, 179, 8, 0.3);
  color: #fde047;
}
.cat-notification::before { background: var(--accent-yellow); }
.cat-notification:hover { background: rgba(234, 179, 8, 0.2); border-color: var(--accent-yellow); }

.cat-reporting {
  background: rgba(59, 130, 246, 0.1);
  border: 1px solid rgba(59, 130, 246, 0.3);
  color: #93c5fd;
}
.cat-reporting::before { background: var(--accent-blue); }
.cat-reporting:hover { background: rgba(59, 130, 246, 0.2); border-color: var(--accent-blue); }

.cat-exceptional {
  background: rgba(239, 68, 68, 0.1);
  border: 1px solid rgba(239, 68, 68, 0.3);
  color: #fca5a5;
}
.cat-exceptional::before { background: var(--accent-red); }
.cat-exceptional:hover { background: rgba(239, 68, 68, 0.2); border-color: var(--accent-red); }

.cat-ui {
  background: rgba(20, 184, 166, 0.1);
  border: 1px solid rgba(20, 184, 166, 0.3);
  color: #5eead4;
}
.cat-ui::before { background: var(--accent-teal); }
.cat-ui:hover { background: rgba(20, 184, 166, 0.2); border-color: var(--accent-teal); }

/* Feature card base styles */
.feature-card {
  padding: 0.5rem 0.6rem;
  border-radius: 6px;
  font-size: 0.75rem;
  font-weight: 500;
  cursor: grab;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
  user-select: none;
  line-height: 1.3;
  flex-shrink: 0;
  width: 100%;
}

.feature-card:active { cursor: grabbing; }

.feature-card.dragging {
  opacity: 0.5;
  transform: scale(0.95);
}

.feature-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 3px;
  height: 100%;
  border-radius: 3px 0 0 3px;
}

.feature-card:hover {
  transform: translateY(-2px) scale(1.02);
  box-shadow: 0 8px 20px -8px rgba(0, 0, 0, 0.5);
}

/* Sprint cell styles */
.sprint-cell {
  padding: 0.4rem;
  border-bottom: 1px solid var(--border);
  border-left: 1px solid rgba(30, 41, 59, 0.5);
  display: flex;
  flex-direction: column;
  gap: 0.3rem;
  transition: all 0.3s ease;
  min-height: 100px;
  background: transparent;
}

.sprint-cell.drag-over {
  background: rgba(34, 197, 94, 0.15);
  border: 2px dashed var(--accent-green);
  border-radius: 6px;
}

/* Add feature button */
.add-feature-btn {
  padding: 0.4rem 0.8rem;
  background: transparent;
  border: 2px dashed rgba(0, 212, 255, 0.3);
  border-radius: 6px;
  color: var(--accent-cyan);
  font-size: 0.75rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.3rem;
}

.add-feature-btn:hover {
  background: rgba(0, 212, 255, 0.1);
  border-style: solid;
  border-color: var(--accent-cyan);
  transform: translateY(-2px);
}

/* Category label */
.category-label {
  padding: 0.75rem;
  font-weight: 600;
  font-size: 0.8rem;
  color: var(--text-primary);
  display: flex;
  align-items: center;
  gap: 0.5rem;
  border-bottom: 1px solid var(--border);
  background: var(--bg-dark);
}

.category-icon {
  width: 28px;
  height: 28px;
  border-radius: 6px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1rem;
}

/* Header cell */
.header-cell {
  padding: 0.75rem;
  text-align: center;
  border-bottom: 2px solid var(--border);
  background: var(--bg-dark);
  position: sticky;
  top: 0;
  z-index: 100;
}

/* Toast styles */
.toast {
  padding: 0.75rem 1.5rem;
  background: var(--bg-card);
  border-radius: 8px;
  font-size: 0.85rem;
  box-shadow: 0 20px 40px -10px rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  gap: 0.5rem;
}

.toast-success {
  border: 1px solid var(--accent-green);
  color: var(--accent-green);
}

.toast-error {
  border: 1px solid var(--accent-red);
  color: var(--accent-red);
}

.toast-warning {
  border: 1px solid var(--accent-yellow);
  color: var(--accent-yellow);
}

.toast-info {
  border: 1px solid var(--accent-cyan);
  color: var(--accent-cyan);
}

/* Animations */
@keyframes fadeInDown {
  from { opacity: 0; transform: translateY(-30px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideIn {
  from { opacity: 0; transform: translateX(-20px); }
  to { opacity: 1; transform: translateX(0); }
}

@keyframes pulse {
  0%, 100% { opacity: 1; transform: scale(1); }
  50% { opacity: 0.5; transform: scale(1.2); }
}

/* Scrollbar styling */
::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

::-webkit-scrollbar-track {
  background: var(--bg-dark);
}

::-webkit-scrollbar-thumb {
  background: var(--border);
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: var(--text-muted);
}

/* Line clamp utility */
.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

